	// const navigation = useNavigation();
	const token = useLoaderData();
	useEffect();
	if (!token) {
		return;
	}
	setTimeout(() => {}, []);
	return (
		<>
			<MainNavigation />
			<main>
				{/* {navigation.state === 'loading' && <p>Loading...</p>} */}
				<Outlet />
			</main>
		</>
	);
}

export default RootLayout;
